contracts/StakingFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./StakingRewards.sol";
import "./StakingToken.sol";

/**
 * @title StakingFactory
 * @dev Factory contract for deploying staking pools
 */
contract StakingFactory {
    struct StakingPool {
        address stakingContract;
        address stakingToken;
        address rewardsToken;
        address creator;
        uint256 createdAt;
    }

    StakingPool[] public stakingPools;
    mapping(address => address[]) public userPools;

    event StakingPoolCreated(
        address indexed creator,
        address indexed stakingContract,
        address stakingToken,
        address rewardsToken,
        uint256 rewardRate
    );

    event TokenDeployed(
        address indexed creator,
        address indexed tokenAddress,
        string name,
        string symbol
    );

    /**
     * @dev Deploy a new ERC20 token
     */
    function deployToken(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) external returns (address) {
        StakingToken token = new StakingToken(name, symbol, initialSupply);
        token.transferOwnership(msg.sender);

        emit TokenDeployed(msg.sender, address(token), name, symbol);
        return address(token);
    }

    /**
     * @dev Create a new staking pool
     */
    function createStakingPool(
        address _stakingToken,
        address _rewardsToken,
        uint256 _rewardRate
    ) external returns (address) {
        require(_stakingToken != address(0), "Invalid staking token");
        require(_rewardsToken != address(0), "Invalid rewards token");
        require(_rewardRate > 0, "Reward rate must be > 0");

        StakingRewards stakingContract = new StakingRewards(
            _stakingToken,
            _rewardsToken,
            _rewardRate
        );

        stakingContract.transferOwnership(msg.sender);

        StakingPool memory pool = StakingPool({
            stakingContract: address(stakingContract),
            stakingToken: _stakingToken,
            rewardsToken: _rewardsToken,
            creator: msg.sender,
            createdAt: block.timestamp
        });

        stakingPools.push(pool);
        userPools[msg.sender].push(address(stakingContract));

        emit StakingPoolCreated(
            msg.sender,
            address(stakingContract),
            _stakingToken,
            _rewardsToken,
            _rewardRate
        );

        return address(stakingContract);
    }

    /**
     * @dev Get total number of staking pools
     */
    function getPoolCount() external view returns (uint256) {
        return stakingPools.length;
    }

    /**
     * @dev Get pools created by a user
     */
    function getUserPools(
        address user
    ) external view returns (address[] memory) {
        return userPools[user];
    }

    /**
     * @dev Get pool info by index
     */
    function getPoolInfo(
        uint256 index
    )
        external
        view
        returns (
            address stakingContract,
            address stakingToken,
            address rewardsToken,
            address creator,
            uint256 createdAt
        )
    {
        require(index < stakingPools.length, "Invalid index");
        StakingPool memory pool = stakingPools[index];
        return (
            pool.stakingContract,
            pool.stakingToken,
            pool.rewardsToken,
            pool.creator,
            pool.createdAt
        );
    }
}

contracts/StakingRewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

/**
 * @title StakingRewards
 * @dev Staking contract that allows users to stake tokens and earn rewards
 */
contract StakingRewards is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    IERC20 public stakingToken;
    IERC20 public rewardsToken;

    uint256 public rewardRate; // Rewards per second
    uint256 public lastUpdateTime;
    uint256 public rewardPerTokenStored;
    uint256 public totalStaked;

    mapping(address => uint256) public stakedBalance;
    mapping(address => uint256) public userRewardPerTokenPaid;
    mapping(address => uint256) public rewards;

    event Staked(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event RewardPaid(address indexed user, uint256 reward);
    event RewardRateUpdated(uint256 newRate);

    constructor(
        address _stakingToken,
        address _rewardsToken,
        uint256 _rewardRate
    ) Ownable(msg.sender) {
        stakingToken = IERC20(_stakingToken);
        rewardsToken = IERC20(_rewardsToken);
        rewardRate = _rewardRate;
        lastUpdateTime = block.timestamp;
    }

    /**
     * @dev Calculate reward per token
     */
    function rewardPerToken() public view returns (uint256) {
        if (totalStaked == 0) {
            return rewardPerTokenStored;
        }
        return
            rewardPerTokenStored +
            (((block.timestamp - lastUpdateTime) * rewardRate * 1e18) /
                totalStaked);
    }

    /**
     * @dev Calculate earned rewards for an account
     */
    function earned(address account) public view returns (uint256) {
        return
            ((stakedBalance[account] *
                (rewardPerToken() - userRewardPerTokenPaid[account])) / 1e18) +
            rewards[account];
    }

    /**
     * @dev Update reward variables
     */
    modifier updateReward(address account) {
        rewardPerTokenStored = rewardPerToken();
        lastUpdateTime = block.timestamp;

        if (account != address(0)) {
            rewards[account] = earned(account);
            userRewardPerTokenPaid[account] = rewardPerTokenStored;
        }
        _;
    }

    /**
     * @dev Stake tokens
     * @param amount Amount of tokens to stake
     */
    function stake(
        uint256 amount
    ) external nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot stake 0");
        require(
            stakingToken.allowance(msg.sender, address(this)) >= amount,
            "Insufficient allowance"
        );
        require(
            stakingToken.balanceOf(msg.sender) >= amount,
            "Insufficient balance"
        );

        totalStaked += amount;
        stakedBalance[msg.sender] += amount;

        stakingToken.safeTransferFrom(msg.sender, address(this), amount);

        emit Staked(msg.sender, amount);
    }

    /**
     * @dev Withdraw staked tokens
     * @param amount Amount of tokens to withdraw
     */
    function withdraw(
        uint256 amount
    ) public nonReentrant updateReward(msg.sender) {
        require(amount > 0, "Cannot withdraw 0");
        require(stakedBalance[msg.sender] >= amount, "Insufficient balance");

        totalStaked -= amount;
        stakedBalance[msg.sender] -= amount;

        stakingToken.safeTransfer(msg.sender, amount);

        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @dev Claim rewards
     */
    function getReward() public nonReentrant updateReward(msg.sender) {
        uint256 reward = rewards[msg.sender];
        if (reward > 0) {
            rewards[msg.sender] = 0;
            rewardsToken.safeTransfer(msg.sender, reward);
            emit RewardPaid(msg.sender, reward);
        }
    }

    /**
     * @dev Exit: withdraw all staked tokens and claim rewards
     */
    function exit() external {
        withdraw(stakedBalance[msg.sender]);
        getReward();
    }

    /**
     * @dev Update reward rate (only owner)
     * @param _rewardRate New reward rate per second
     */
    function setRewardRate(
        uint256 _rewardRate
    ) external onlyOwner updateReward(address(0)) {
        rewardRate = _rewardRate;
        emit RewardRateUpdated(_rewardRate);
    }

    /**
     * @dev Get staking info for an account
     */
    function getStakingInfo(
        address account
    )
        external
        view
        returns (
            uint256 staked,
            uint256 earnedRewards,
            uint256 totalStakedAmount,
            uint256 currentRewardRate
        )
    {
        return (
            stakedBalance[account],
            earned(account),
            totalStaked,
            rewardRate
        );
    }

    function getAccountStatus(
        address account
    ) external view returns (uint256 balance, uint256 allowance, uint256 staked) {
        return (
            stakingToken.balanceOf(account),
            stakingToken.allowance(account, address(this)),
            stakedBalance[account]
        );
    }
}

contracts/StakingToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title StakingToken
 * @dev ERC20 token that can be used for staking and rewards
 */
contract StakingToken is ERC20, Ownable {
    constructor(
        string memory name,
        string memory symbol,
        uint256 initialSupply
    ) ERC20(name, symbol) Ownable(msg.sender) {
        _mint(msg.sender, initialSupply * 10 ** decimals());
    }

    /**
     * @dev Mint new tokens (only owner)
     * @param to Address to receive the tokens
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        _mint(to, amount);
    }

    /**
     * @dev Burn tokens
     * @param amount Amount of tokens to burn
     */
    function burn(uint256 amount) external {
        _burn(msg.sender, amount);
    }
}

